import { Component } from '@angular/core';
import { Giocattolo } from './model/Giocattolo';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'giocattoli';
  giocattolo !: Giocattolo;


  formatLabel(value: number): string {
    if (value >= 1000) {
      return Math.round(value / 1000) + 'k';
    }

    return `${value}`;
  }

}

<div>
  <p>Lista Giocattoli</p>
</div>


<mat-card-title-group>
  <mat-card-title>Card Title</mat-card-title>
  <img mat-card-image> https://www.b2x.it/rest/images/2023/11/15/1488336.jpg?imageFormat=@1x
</mat-card-title-group>

<mat-slider min="0" max="10" step="1" showTickMarks discrete [displayWith]="formatLabel">
  <input matSliderThumb>
</mat-slider>
